args:
"
.cta-banner {
    text-align: center;
    padding: 64px 24px;
    margin: 48px 0;
    background: var(--info-soft);
    border-radius: var(--radius);
    color: var(--text);
}

.cta-banner h2 {
    font-size: 32px;
    margin-bottom: 16px;
    color: var(--text);
}

.cta-banner p {
    font-size: 18px;
    color: var(--muted);
    max-width: 600px;
    margin: 0 auto;
}
"
